-- 导出  表 reading_notes.book 结构
CREATE TABLE IF NOT EXISTS `book` (
  `id` bigint(20) NOT NULL,
  `user_id` bigint(20) NOT NULL COMMENT '用户ID',
  `read_date` date NOT NULL COMMENT '阅读日期',
  `title` varchar(100) NOT NULL COMMENT '标题',
  `subtitle` varchar(100) DEFAULT NULL COMMENT '副标题',
  `translators` varchar(100) DEFAULT NULL COMMENT '译者',
  `publish_date` date DEFAULT NULL COMMENT '出版日期',
  `binding` varchar(128) DEFAULT NULL COMMENT '装订',
  `pages` varchar(10) DEFAULT NULL COMMENT '页数',
  `image` varchar(100) DEFAULT NULL COMMENT '封面图片',
  `summary` varchar(4096) DEFAULT NULL COMMENT '概述',
  `publisher` varchar(100) DEFAULT NULL COMMENT '出版社',
  `isbn` char(13) DEFAULT NULL COMMENT 'ISBN',
  `price` varchar(10) DEFAULT NULL COMMENT '价格',
  `douban` varchar(100) DEFAULT NULL COMMENT '豆瓣链接',
  `rating` varchar(10) DEFAULT NULL COMMENT '评分',
  `create_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='书籍';

